    private void btnAceptarNueActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAceptarNueActionPerformed
       
        String newdni=txtNuevoDni.getText();
        String newnombre=txtNuevoNom.getText();
        String newape = txtNuevoApe.getText();
 
        Cliente clie = new Cliente();
        clie.setDni(Integer.parseInt(newdni));
        clie.setNombres(newnombre);
        clie.setApellidos(newape);
        Principal.cc.agregarCliente(clie);
        Principal.cboDNI.removeAllElements();
        for(int i=0;i<Principal.cc.getnClientes();i++){
            Principal.cboDNI.addElement(Principal.cc.obtenerCliente(i).getDni());
        }
        
        Principal.cboDniClie.setModel(Principal.cboDNI);
        txtNuevoDni.setText(" ");
        txtNuevoNom.setText(" ");
        txtNuevoApe.setText(" ");
        txtNuevoDni.requestFocus();
        // TODO add your handling code here:
 
    }//GEN-LAST:event_btnAceptarNueActionPerformed

