    private void txtNuevoDniFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_txtNuevoDniFocusLost

        String dniclie = txtNuevoDni.getText();
        for(int i=0; i< Principal.cc.getnClientes();i++){
            if(Principal.cc.obtenerCliente(i).getDni()== Integer.parseInt(dniclie)){
                JOptionPane.showMessageDialog(this, "El DNI: "+ dniclie + "ya existe.","Validacion",JOptionPane.ERROR_MESSAGE );
                txtNuevoDni.setText(" ");
                txtNuevoDni.setFocusable(true);
            }
        }
        
        // TODO add your handling code here:
    }//GEN-LAST:event_txtNuevoDniFocusLost

